mapper.update:
update
	   member
   set emp_pwd = #{empPwd}
 where emp_no = #{empNo}

mapper.insert:
insert
  into member
  (
    emp_no
  , emp_name
  , emp_pwd
  , phone
  , email
  , profile_originname
  , profile_path
  , job_no
  , dept_no
  )
  values
  (
    #{empNo}
  , #{empName}
  , #{empPwd}
  , #{phone}
  , #{email}
  , #{originName}
  , #{path}
  , #{jobNo}
  , #{deptNo}
  )
