mapper.insert:
insert
  into notice
      (
        notice_no
      , notice_title
      , notice_content
      , notice_originname
      , notice_filepath
      )
values
      (
        seq_nno.nextval
      , #{noticeTitle}
      , #{noticeContent}
      , #{noticeOrigin}
      , #{noticePath}
      )

mapper.update:
update 
       notice
   set notice_title = #{noticeTitle}
     , notice_content = #{noticeContent}
     , notice_mdate = sysdate
     , notice_originname = #{noticeOrigin}
 where notice_no = #{noticeNo}
