mapper.update:
update
       appr_form
   set form_title = #{formTitle}
     , form_content = #{formContent}
     , form_status = #{formStatus}
     , form_info = #{formInfo}
     , modify_date = sysdate
 where form_no = #{formNo}

mapper.insert:
insert 
  into appr_form
  (
    form_no
  , form_title
  , form_content
  , form_status
  , form_info
  )
  values
  (
    seq_afno.nextval
  , #{formTitle}
  , #{formContent}
  , #{formStatus}
  , #{formInfo}
  )
